import { Component, Input, OnInit } from '@angular/core';
import { SandboxService } from 'src/app/shared/services/sandbox.service';
import { LoginComponent } from '../../login/login.component';
import { Observable } from 'rxjs';
import { Result } from '../../shared/models/result';

@Component({
  selector: 'app-inicio',
  templateUrl: './inicio.component.html',
  styleUrls: ['./inicio.component.css'],
})
export class InicioComponent implements OnInit {
  id: string;
  nombre: string;
  vin: string ;
  comnetario: string ;
  category: string ;

  selectedFiles: FileList ;
  selectedFileNames: string[] = [];

  progressInfos: any[] = [];
  message: string[] = [];

  previews: string[] = [];
  imageInfos?: Observable<any>;
  file: any;
  result: Result= new Result();

  @Input() data: LoginComponent | undefined;
  constructor(private sandboxService: SandboxService) {
    const data = JSON.parse(localStorage.getItem('data') || '{}');
    console.log(data);
    this.id = data.id;
    this.nombre = data.name;
    this.vin = data.vin;
    this.category = data.category;
  }
  selectFiles(event: any): void {
    this.message = [];
    this.progressInfos = [];
    this.selectedFileNames = [];
    this.selectedFiles = event.target.files;
    this.file = event.target.files[0];

    this.previews = [];
    if (this.selectedFiles && this.selectedFiles[0]) {
      const numberOfFiles = this.selectedFiles.length;
      for (let i = 0; i < numberOfFiles; i++) {
        const reader = new FileReader();

        reader.onload = (e: any) => {
          this.previews.push(e.target.result);
        };

        reader.readAsDataURL(this.selectedFiles[i]);

        this.selectedFileNames.push(this.selectedFiles[i].name);
      }
    }
  }
  onGuardarCambios(): void {
    const data = new FormData();
    data.append('vehicle_id', this.id);
    data.append('category',"motor");
    data.append('comment', this.comnetario);
    data.append('path',this.file );

    this.sandboxService.postAbcars(data).subscribe({
      next: (res:any) => {
        this.result = res;

        console.log(res);
      },
      error: (err) => {
        console.log(err);
      },
    });
  }

  ngOnInit(): void {}
}
<div class="container">
  <mat-toolbar>
    <span> Vehiculo: Vin:</span>
  </mat-toolbar>

  <mat-horizontal-stepper>
    <mat-step label="Primer paso">
      <div class="containerInput">
        <mat-form-field class="inputLi">
          <mat-label>Nombre</mat-label>
          <input matInput placeholder="Placeholder" [(ngModel)]="nombre" />
        </mat-form-field>
      </div>

      <div class="containerInput">
        <mat-form-field class="inputLi">
          <mat-label>Vin :</mat-label>
          <input matInput placeholder="Placeholder" [(ngModel)]="vin" />
        </mat-form-field>
      </div>
      <div class="containerInput">
        <mat-form-field class="inputLi">
          <mat-label>Comnetario:</mat-label>
          <textarea matInput placeholder="Placeholder" [(ngModel)]="comnetario" ></textarea>  
        </mat-form-field>
      </div>
      <div>
       
        <mat-form-field class="imgbutton">
          <div>

        
            <input
              type="file"
              id="fileInput"
              name="fileInput"
              accept="image/*" multiple 
              (change)="selectFiles($event)"
            />
          </div>
        </mat-form-field>
       
      </div>

      <button mat-flat-button color="accent" (click)="onGuardarCambios()">Guardar cambios</button>
      <div>
        <img *ngFor='let preview of previews' [src]="preview" class="preview">
      </div>
      
      <div *ngIf="message.length" class="message">
        <ul *ngFor="let msg of message; let i = index">
          <li>{{ msg }}</li>
        </ul>
      </div>
      
      <mat-card class="list-card"> 
        <mat-card-content>
          <mat-list role="list">
            <mat-list-item role="listitem" *ngFor="let image of imageInfos | async" class="list-item">
              <p matLine><a href="{{ image.url }}">{{ image.name }}</a></p>
              <img src="{{ image.url }}" alt="{{ image.name }}" height="50px"/>
            </mat-list-item>
          </mat-list>
        </mat-card-content>
      </mat-card>
      
      <mat-label>Comnetario: {{result.comment }} </mat-label>
      <br>
      <mat-label>Mensaje: {{result.message}} </mat-label>
      <br>
 
        <img src="https://sandbox.abcars.mx/abcars-backend/api/check_images/{{result.image}}"  height="100px"/>

     
    </mat-step>

    <mat-step label="Segundo paso"> </mat-step>

    <mat-step label="tercero paso"> </mat-step>

    <mat-step label="cuarto paso"> </mat-step>
  </mat-horizontal-stepper>
</div>
